// src/components/Hero.js
import React from 'react';

export default function Hero() {
  return (
    <section className="bg-blue-50 min-h-screen flex flex-col justify-center items-center text-center px-4">
      <h1 className="text-5xl font-bold mb-4">Aprenda Nutrição com o Dr. Fábio</h1>
      <p className="text-lg mb-6 max-w-xl">
        Cursos e mentorias exclusivos para transformar sua alimentação e estilo de vida.
      </p>
      <a
        href="/login"
        className="bg-blue-500 text-white px-6 py-3 rounded-lg text-lg hover:bg-blue-600"
      >
        Comece Agora
      </a>
    </section>
  );
}
