// src/components/Features.js
import React from 'react';

export default function Features() {
  const features = [
    { title: 'Cursos Exclusivos', desc: 'Conteúdo completo e atualizado.' },
    { title: 'Mentorias Personalizadas', desc: 'Acompanhamento individual com o nutrólogo.' },
    { title: 'Acesso Online', desc: 'Assista de qualquer lugar, a qualquer hora.' },
  ];

  return (
    <section className="py-16 px-4 bg-white">
      <h2 className="text-3xl font-bold text-center mb-12">Diferenciais</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {features.map((f, i) => (
          <div key={i} className="p-6 bg-blue-50 rounded shadow hover:scale-105 transition">
            <h3 className="font-bold text-xl mb-2">{f.title}</h3>
            <p>{f.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
