// src/components/CoursesCarousel.js
import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';

export default function CoursesCarousel() {
  const courses = [
    { title: 'Nutrição Básica', desc: 'Aprenda fundamentos.' },
    { title: 'Alimentação Saudável', desc: 'Equilibre sua dieta.' },
    { title: 'Planejamento de Refeições', desc: 'Organize suas refeições.' },
    { title: 'Suplementação', desc: 'Use suplementos de forma consciente.' },
  ];

  return (
    <section id="cursos" className="py-16 px-4 bg-white">
      <h2 className="text-3xl font-bold text-center mb-8">Cursos</h2>
      <Swiper
        spaceBetween={20}
        slidesPerView={1}
        breakpoints={{
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 3 },
        }}
      >
        {courses.map((c, i) => (
          <SwiperSlide key={i}>
            <div className="p-6 bg-gradient-to-br from-blue-100 to-blue-200 rounded-xl shadow-lg hover:scale-105 transition-transform duration-300">
              <h3 className="font-bold text-xl mb-2">{c.title}</h3>
              <p>{c.desc}</p>
              <button className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition transform hover:-translate-y-1">
                Saiba Mais
              </button>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}
