import React, { useState } from 'react';
import CourseModal from './CourseModal';
import ModuleModal from './ModuleModal';

export default function Courses() {
  const [courses, setCourses] = useState([]);
  const [courseModalOpen, setCourseModalOpen] = useState(false);
  const [moduleModalOpen, setModuleModalOpen] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [editingModule, setEditingModule] = useState(null);
  const [currentCourseId, setCurrentCourseId] = useState(null);

  const handleSaveCourse = (course) => {
    setCourses((prev) => {
      const exists = prev.find((c) => c.id === course.id);
      if (exists) return prev.map((c) => (c.id === course.id ? course : c));
      return [...prev, course];
    });
  };

  const handleDeleteCourse = (id) => {
    if (confirm('Deseja realmente excluir o curso?'))
      setCourses((prev) => prev.filter((c) => c.id !== id));
  };

  const handleOpenModuleModal = (courseId, module = null) => {
    setCurrentCourseId(courseId);
    setEditingModule(module);
    setModuleModalOpen(true);
  };

  const handleSaveModule = (module) => {
    setCourses((prev) =>
      prev.map((c) => {
        if (c.id === currentCourseId) {
          const exists = c.modules?.find((m) => m.id === module.id);
          const modules = c.modules || [];
          const newModules = exists
            ? modules.map((m) => (m.id === module.id ? module : m))
            : [...modules, module];
          return { ...c, modules: newModules };
        }
        return c;
      })
    );
  };

  const toggleLesson = (courseId, moduleId, lessonId) => {
    setCourses((prev) =>
      prev.map((c) => {
        if (c.id === courseId) {
          const modules = c.modules.map((m) => {
            if (m.id === moduleId) {
              const lessons = m.lessons.map((l) =>
                l.id === lessonId ? { ...l, completed: !l.completed } : l
              );
              return { ...m, lessons };
            }
            return m;
          });
          return { ...c, modules };
        }
        return c;
      })
    );
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold">Cursos</h2>
        <button
          className="bg-green-500 text-white px-4 py-2 rounded"
          onClick={() => setCourseModalOpen(true)}
        >
          Novo Curso
        </button>
      </div>

      {courses.map((c) => (
        <div key={c.id} className="bg-white p-4 rounded shadow mb-4">
          <div className="flex justify-between items-center">
            <div>
              <h3 className="font-bold">{c.title}</h3>
              <p>{c.description}</p>
            </div>
            <div className="flex gap-2">
              <button
                className="bg-blue-500 text-white px-2 py-1 rounded"
                onClick={() => { setEditingCourse(c); setCourseModalOpen(true); }}
              >
                Editar
              </button>
              <button
                className="bg-red-500 text-white px-2 py-1 rounded"
                onClick={() => handleDeleteCourse(c.id)}
              >
                Excluir
              </button>
              <button
                className="bg-purple-500 text-white px-2 py-1 rounded"
                onClick={() => handleOpenModuleModal(c.id)}
              >
                Novo Módulo
              </button>
            </div>
          </div>

          {/* Módulos */}
          <div className="mt-4 ml-4">
            {c.modules?.map((m) => (
              <div key={m.id} className="mb-2">
                <div className="flex justify-between items-center">
                  <h4 className="font-semibold">{m.title}</h4>
                  <button
                    className="bg-blue-400 text-white px-2 py-1 rounded"
                    onClick={() => handleOpenModuleModal(c.id, m)}
                  >
                    Editar
                  </button>
                </div>
                <ul className="ml-4">
                  {m.lessons.map((l) => (
                    <li key={l.id} className="flex items-center gap-2">
                      <input
                        type="checkbox"
                        checked={l.completed}
                        onChange={() => toggleLesson(c.id, m.id, l.id)}
                      />
                      {l.title}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      ))}

      <CourseModal
        isOpen={courseModalOpen}
        onClose={() => { setCourseModalOpen(false); setEditingCourse(null); }}
        onSave={handleSaveCourse}
        course={editingCourse}
      />

      <ModuleModal
        isOpen={moduleModalOpen}
        onClose={() => { setModuleModalOpen(false); setEditingModule(null); }}
        onSave={handleSaveModule}
        module={editingModule}
      />
    </div>
  );
}
