// src/components/Testimonials.js
import React from 'react';

export default function Testimonials() {
  const testimonials = [
    { name: 'Maria Silva', text: 'Os cursos do Dr. Fábio mudaram minha alimentação!' },
    { name: 'João Pereira', text: 'As mentorias são incríveis e muito práticas.' },
    { name: 'Ana Costa', text: 'Aprendi hábitos saudáveis que mantive até hoje.' },
  ];

  return (
    <section id="depoimentos" className="py-16 px-4 bg-gray-100">
      <h2 className="text-3xl font-bold text-center mb-12">Depoimentos</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {testimonials.map((t, i) => (
          <div key={i} className="p-6 bg-white rounded shadow">
            <p className="italic mb-4">"{t.text}"</p>
            <p className="font-bold text-right">- {t.name}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
