// src/components/Hero.js
import React from 'react';

export default function Hero() {
  return (
    <section className="relative h-screen bg-blue-50 flex flex-col justify-center items-center text-center px-4">
      <div className="absolute inset-0 bg-cover bg-center opacity-20" style={{ backgroundImage: 'url(https://source.unsplash.com/1600x900/?nutrition,food)' }}></div>
      <div className="relative z-10 max-w-3xl">
        <h1 className="text-5xl md:text-6xl font-bold mb-4 animate-fadeIn">Transforme sua Nutrição com Dr. Fábio</h1>
        <p className="text-lg mb-6 animate-fadeIn delay-200">Cursos e mentorias exclusivas para você atingir seu melhor estilo de vida.</p>
        <a
          href="/login"
          className="bg-blue-500 text-white px-6 py-3 rounded-lg text-lg hover:bg-blue-600 transition animate-fadeIn delay-400"
        >
          Comece Agora
        </a>
      </div>
    </section>
  );
}
