// src/components/TestimonialsCarousel.js
import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';

export default function TestimonialsCarousel() {
  const testimonials = [
    { name: 'Maria Silva', text: 'Mudou minha alimentação!' },
    { name: 'João Pereira', text: 'Mentorias incríveis!' },
    { name: 'Ana Costa', text: 'Aprendi hábitos saudáveis.' },
    { name: 'Pedro Lima', text: 'Conteúdo de qualidade!' },
  ];

  return (
    <section id="depoimentos" className="py-16 px-4 bg-gray-100">
      <h2 className="text-3xl font-bold text-center mb-8">Depoimentos</h2>
      <Swiper
        spaceBetween={20}
        slidesPerView={1}
        breakpoints={{ 768: { slidesPerView: 2 }, 1024: { slidesPerView: 3 } }}
      >
        {testimonials.map((t, i) => (
          <SwiperSlide key={i}>
            <div className="p-6 bg-white rounded-xl shadow-lg hover:shadow-2xl transition-shadow duration-300">
              <p className="italic mb-4">"{t.text}"</p>
              <p className="font-bold text-right">- {t.name}</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
}
